from microbit import *import dht11pin14.write_digital(0)  # 把pin14端口置低,反向控制使用#*****************************WIFI模块内部代码***************************************************class Obloq(object):    def connectWifi(self,SSID,PASSWORD,TIME):        uart.write('AT+CWJAP="%s","%s"\r\n' %(SSID, PASSWORD))      # 设置wifi模块连接无线网络        display.scroll("wait...");display.show(Image.HAPPY)        uart.write('AT+CIPMUX=0\r\n');sleep(500)                   # 设置WIFI模块单路链接模式        uart.write('AT+CIPSTART="TCP","0",0\r\n');sleep(TIME)           if uart.any():                                              #第二次读，返回服务器IP设置消息            data = str(uart.readall(), "UTF-8");sleep(1000)            uart.write('AT+CIFSR\r\n');sleep(2000);data=0               # 设置这个后 读串口返回绑定ip         if uart.any():            data = str(uart.readall(), "UTF-8")            temp = data.split("\"",5)            self.ip_address = temp[1]            if self.ip_address[0]=='1' and len(self.ip_address)>10:                 return True            else:                display.show(".");sleep(3000)                return False             else:            display.show(".");sleep(3000)            return False                            def ifconfig(self):        return self.ip_address                   def httpSet(self,IP,PORT):        uart.write('AT+CIPSTART="TCP","%s",%s\r\n' %(IP, PORT))          sleep(6000)        uart.write('AT+CIPMODE=1\r\n')  # 设置WIFI模块为透穿模式        sleep(500)        uart.write('AT+CIPSEND\r\n')  # 在透穿模式下开始发送数据        sleep(2000)        if uart.any():  # 如果串口有值            self.res = str(uart.readall(), "UTF-8")            self.res = 0          def get(self,url,time):        uart.write('GET /'+ url + '\r\n\r\n')          sleep(1000)        if uart.any():  # 如果串口有值            res = str(uart.readall(), "UTF-8")              return (200,res)        return (404,"NOT FOUND")  Obloq = Obloq()#***********************************************************************************************IP="192.168.43.64"   PORT="8080"SSID="Redmi Note"PASSWORD="zx3097559"uart.init(baudrate=115200, bits=8, parity=None, stop=1, tx=pin12, rx=pin8)  while Obloq.connectWifi(SSID,PASSWORD,10000) != True:    display.show(".")display.scroll(Obloq.ifconfig())Obloq.httpSet(IP,PORT)while True:    temp,hum=dht11.read(1)      # 获取1号口温湿度数据    light =pin2.read_analog()   # 获取2号口光线数据    erron,resp = Obloq.get('input?id=1&tempval=' + str(temp) + '&lightval=' + str(light) + '&humval=' + str(hum), 10000)     if erron == 200:        display.scroll(resp)    else:        display.scroll(str(erron))    sleep(1000*5)'''   #****************客户端http反向控制智能终端******************************************    # 如果使用此功能，请将上一句延迟代码去掉    for i in range(10):        uart.write('GET /inputs'+'\r\n\r\n')  # 向服务器发送GET请求，请求路径/inputs        sleep(500)        if uart.any():  # 如果串口有值            response = str(uart.readall(), "UTF-8")  # 读取所有值             if response == "turn":      # 如果是“turn”                pin14.write_digital(1)   # pin14口置高电平            elif response == "off":     # 如果是“off”                pin14.write_digital(0)   #pin14口置底电平            else:                continue'''             